import React from 'react';

import { Cards, CountryPicker, Chart } from './components';
import { fetchData } from './api/';
import styles from './App.module.css';
import MailchimpSubscribe from "react-mailchimp-subscribe"
import image from './images/image.png';
import FooterPagePro from './components/footer'
import { MDBCol, MDBContainer, MDBRow, MDBFooter,footer } from "mdbreact";



class App extends React.Component {
    state = {
        data: {},
        country: '',
    }

    async componentDidMount() {
        const data = await fetchData();

        this.setState({ data });
    }

    handleCountryChange = async(country) => {
        const data = await fetchData(country);

        this.setState({ data, country: country });
    }

    render() {
        const { data, country } = this.state;

        return (
          <div>
          <div className = { styles.container } >
          <h5 style={{color:'#152F8D'}}> Subscribe to our email newsletter today to receive  latest news on Covid-19 </h5>
          <MailchimpSubscribe url = { process.env.REACT_APP_MAILCHIMP_URL }/>
          <img className = { styles.image }src = { image } alt = "COVID-19"/>
          <Cards data = { data }/>
          <CountryPicker handleCountryChange = { this.handleCountryChange }/>
          <Chart data = { data } country = { country }/>
          </div>



        <footer class="page-footer font-small blue">
        <div class="primary-color">
        <div class="container">

            <div class="row py-4 d-flex align-items-center">


                <div class="col-md-6 col-lg-5 text-center text-md-left mb-4 mb-md-0">
                    <h6 class="mb-0 white-text">Get connected with us on social networks!</h6>
                </div>

                <div class="col-md-6 col-lg-7 text-center text-md-right">

                    <a class="fb-ic ml-0">
                        <i class="fab fa-facebook-f white-text mr-4"> </i>
                    </a>

                    <a class="tw-ic">
                        <i class="fab fa-twitter white-text mr-4"> </i>
                    </a>

                    <a class="gplus-ic">
                        <i class="fab fa-google-plus-g white-text mr-4"> </i>
                    </a>

                    <a class="li-ic">
                        <i class="fab fa-linkedin-in white-text mr-4"> </i>
                    </a>

                    <a class="ins-ic">
                        <i class="fab fa-instagram white-text mr-lg-4"> </i>
                    </a>
                </div>


            </div>

        </div>
        </div>
        </footer>


          </div>
        );
    }
}

export default App;
